import React, { useState, useMemo } from "react";

const ExportData = () => {
  const [option, setOption] = useState("All");
  const [reviewer, setReviewer] = useState([]);

  const sites = useMemo(() => [
    "ABC Hospital",
    "Green Valley Medical Center",
    "Sunnydale Hospital",
    "Riverbend Health Clinic",
    "Mountain Peak Hospital",
    "Lakeside Community Hospital",
  ], []);

  const visits = useMemo(() => [
    "Baseline", "Visit 1", "Visit 2", "Visit 3", "USV 1", "EOT"
  ], []);

  const reviewers = ["Reader 1", "Reader 2", "Adjudicator"];

  const renderCheckboxList = (items) => items.map((item, i) => (
    <label key={i} style={{ display: 'block', marginBottom: 4 }}>
      <input type="checkbox" /> {item}
    </label>
  ));

  return (
    <div style={{ padding: 16, fontFamily: 'sans-serif', fontSize: 14 }}>
      <h3>Export Data</h3>
      <div style={{ display: "flex", gap: 12, flexWrap: 'wrap' }}>
        {[
          { title: "Site", data: sites },
          { title: "Participant", data: Array.from({ length: 5 }, (_, i) => `Participant ${i + 1}`) },
          { title: "Visit", data: visits },
          { title: "Form version", data: ["Version 1", "Version 2", "Version 3"] },
          { title: "Item OID / Form version", data: ["OID 1 / Ver 1", "OID 2 / Ver 2", "OID 3 / Ver 3"] }
        ].map((section, idx) => (
          <div key={idx}>
            <strong>{section.title}</strong>
            <input placeholder="search" style={{ width: "100%", marginBottom: 4 }} />
            {renderCheckboxList(section.data)}
          </div>
        ))}
      </div>

      <div style={{ marginTop: 16 }}>
        <strong>Option</strong><br />
        {["All", "Softlock", "Without softlock"].map(opt => (
          <label key={opt} style={{ marginRight: 10 }}>
            <input
              type="radio"
              name="option"
              value={opt}
              checked={option === opt}
              onChange={() => setOption(opt)}
            /> {opt}
          </label>
        ))}
      </div>

      <div style={{ marginTop: 16 }}>
        <strong>Reviewer</strong><br />
        {reviewers.map(r => (
          <label key={r} style={{ marginRight: 10 }}>
            <input
              type="checkbox"
              value={r}
              checked={reviewer.includes(r)}
              onChange={(e) => {
                const checked = e.target.checked;
                setReviewer(prev =>
                  checked ? [...prev, r] : prev.filter(val => val !== r)
                );
              }}
            /> {r}
          </label>
        ))}
      </div>

      <div style={{ marginTop: 20 }}>
        <button>Preview</button>
        <button style={{ marginLeft: 8 }}>Save</button>
        <button style={{ marginLeft: 8 }}>Export</button>
      </div>
    </div>
  );
};

export default ExportData;
