import React, { useState } from "react";

function QCStatusTable({ data, onQCChange, filter, setFilter, onAddRow, onUpdateRow, editingRow, setEditingRow }) {
  const filteredData = data.filter(item => {
    return (!filter.participant || item.participant.includes(filter.participant)) &&
           (!filter.visit || item.visit.includes(filter.visit)) &&
           (!filter.qc || item.qc === filter.qc);
  });

  return (
    <>
      <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: "1rem" }}>
        <thead>
          <tr style={{ backgroundColor: "#f2f2f2" }}>
            <th style={{ border: "1px solid #ddd", padding: "8px", textAlign: "left" }}>
              Participant<br />
              <input
                value={filter.participant || ''}
                onChange={(e) => setFilter({ ...filter, participant: e.target.value })}
                style={{ width: '100%' }}
              />
            </th>
            <th style={{ border: "1px solid #ddd", padding: "8px", textAlign: "left" }}>
              Visit<br />
              <input
                value={filter.visit || ''}
                onChange={(e) => setFilter({ ...filter, visit: e.target.value })}
                style={{ width: '100%' }}
              />
            </th>
            <th style={{ border: "1px solid #ddd", padding: "8px", textAlign: "left" }}>
              QC<br />
              <select
                value={filter.qc || ''}
                onChange={(e) => setFilter({ ...filter, qc: e.target.value })}
                style={{ width: '100%' }}
              >
                <option value="">All</option>
                <option value="v1">v1</option>
                <option value="v2">v2</option>
                <option value="v3">v3</option>
              </select>
            </th>
          </tr>
        </thead>
        <tbody>
          {filteredData.map((item, idx) => (
            <tr key={idx}>
              <td style={{ border: "1px solid #ddd", padding: "8px", color: "#800040", fontWeight: "bold" }}>{item.participant}</td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>{item.visit}</td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                <select value={item.qc} onChange={(e) => onQCChange(idx, e.target.value)} style={{ padding: "4px", borderRadius: "4px" }}>
                  <option value="v1">v1</option>
                  <option value="v2">v2</option>
                  <option value="v3">v3</option>
                </select>
              </td>
            </tr>
          ))}
          {/* Editing Row */}
          {editingRow && (
            <tr style={{ backgroundColor: "#f0f8ff" }}>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                <input
                  type="text"
                  value={editingRow.participant}
                  onChange={(e) => setEditingRow({ ...editingRow, participant: e.target.value })}
                  style={{ width: "100%", padding: "4px", border: "1px solid #ccc", borderRadius: "2px" }}
                  placeholder="Enter participant"
                />
              </td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                <input
                  type="text"
                  value={editingRow.visit}
                  onChange={(e) => setEditingRow({ ...editingRow, visit: e.target.value })}
                  style={{ width: "100%", padding: "4px", border: "1px solid #ccc", borderRadius: "2px" }}
                  placeholder="Enter visit"
                />
              </td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                <select
                  value={editingRow.qc}
                  onChange={(e) => setEditingRow({ ...editingRow, qc: e.target.value })}
                  style={{ width: "100%", padding: "4px", border: "1px solid #ccc", borderRadius: "2px" }}
                >
                  <option value="v1">v1</option>
                  <option value="v2">v2</option>
                  <option value="v3">v3</option>
                </select>
              </td>
            </tr>
          )}
        </tbody>
      </table>
      
      {/* Add Row Button */}
      <div style={{ marginBottom: "1rem" }}>
        <button
          onClick={onAddRow}
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: "#28a745",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer"
          }}
        >
          Add Row
        </button>
      </div>
    </>
  );
}

export default function QCStatusDialog() {
  const [data, setData] = useState([
    { participant: "ABC1001", visit: "Baseline", qc: "v1" },
    { participant: "ABC1001", visit: "Visit 1", qc: "v1" },
    { participant: "ABC2001", visit: "Baseline", qc: "v1" },
    { participant: "ABC2001", visit: "Visit 2", qc: "v2" },
    { participant: "ABC3001", visit: "Visit 1", qc: "v3" },
    { participant: "ABC3001", visit: "Visit 2", qc: "v1" }
  ]);

  const [filter, setFilter] = useState({ participant: '', visit: '', qc: '' });
  const [editingRow, setEditingRow] = useState(null);

  const handleQCChange = (index, newQC) => {
    const updated = [...data];
    updated[index].qc = newQC;
    setData(updated);
  };

  const handleAddRow = () => {
    setEditingRow({ participant: '', visit: '', qc: 'v1' });
  };

  const handleSaveRow = () => {
    if (editingRow && editingRow.participant && editingRow.visit) {
      setData([...data, editingRow]);
      setEditingRow(null);
    }
  };

  const handleCancelRow = () => {
    setEditingRow(null);
  };

  return (
    <div style={{ maxWidth: "600px", margin: "2rem auto", border: "1px solid #ccc", borderRadius: "8px", padding: "1.5rem", fontFamily: "Arial, sans-serif" }}>
      <h2 style={{ fontSize: "1.2rem", fontWeight: "bold", marginBottom: "0.5rem" }}>Released status</h2>
      <h3 style={{ fontSize: "1rem", fontWeight: "bold", marginBottom: "1rem" }}>QC Version Manager</h3>

      <QCStatusTable 
        data={data} 
        onQCChange={handleQCChange} 
        filter={filter} 
        setFilter={setFilter}
        onAddRow={handleAddRow}
        editingRow={editingRow}
        setEditingRow={setEditingRow}
      />

      {/* Save/Cancel Buttons for Editing Row */}
      {editingRow && (
        <div style={{ marginBottom: "1rem", textAlign: "right" }}>
          <button
            onClick={handleCancelRow}
            style={{
              padding: "0.5rem 1rem",
              marginRight: "0.5rem",
              border: "1px solid #ccc",
              borderRadius: "4px",
              backgroundColor: "#f9f9f9",
              cursor: "pointer"
            }}
          >
            Cancel
          </button>
          <button
            onClick={handleSaveRow}
            style={{
              padding: "0.5rem 1rem",
              border: "1px solid #ccc",
              borderRadius: "4px",
              backgroundColor: "#28a745",
              color: "white",
              cursor: "pointer"
            }}
          >
            Save Row
          </button>
        </div>
      )}

      <div style={{ marginTop: "1.5rem", textAlign: "right" }}>
        <button style={{ padding: "0.5rem 1rem", border: "1px solid #ccc", borderRadius: "4px", backgroundColor: "#f9f9f9" }}>Save All</button>
      </div>
    </div>
  );
}
