      { type: 'feature', text: 'ÏÉàÎ°úÏö¥ ÌïÑÌÑ∞ÎßÅ ÏòµÏÖò Ï∂îÍ∞Ä' },
      { type: 'improvement', text: 'Î∞òÏùëÌòï ÎîîÏûêÏù∏ Í∞úÏÑ†' }
    ]
  },
  {
    id: 2,
    version: 'v3.2.0',
    date: '2024-01-10',
    title: 'ÏÉàÎ°úÏö¥ Í∏∞Îä• Ï∂îÍ∞Ä',
    type: 'feature',
    description: 'ÏÇ¨Ïö©Ïûê ÏöîÏ≤≠Ïóê Îî∞Îùº ÏÉàÎ°úÏö¥ Í∏∞Îä•Îì§Ïù¥ Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§.',
    details: [
      'Ïã§ÏãúÍ∞Ñ ÏïåÎ¶º ÏãúÏä§ÌÖú ÎèÑÏûÖ',
      'Îç∞Ïù¥ÌÑ∞ ÎÇ¥Î≥¥ÎÇ¥Í∏∞ Í∏∞Îä• Í∞ïÌôî',
      'ÏÇ¨Ïö©Ïûê Í∂åÌïú Í¥ÄÎ¶¨ Í∞úÏÑ†',
      'Îã§Íµ≠Ïñ¥ ÏßÄÏõê ÌôïÎåÄ'
    ],
    changes: [
      { type: 'feature', text: 'Ïã§ÏãúÍ∞Ñ ÏïåÎ¶º ÏãúÏä§ÌÖú Íµ¨ÌòÑ' },
      { type: 'feature', text: 'Excel ÎÇ¥Î≥¥ÎÇ¥Í∏∞ Í∏∞Îä• Ï∂îÍ∞Ä' },
      { type: 'improvement', text: 'ÏÇ¨Ïö©Ïûê Ïù∏ÌÑ∞ÌéòÏù¥Ïä§ Í∞úÏÑ†' }
    ]
  },
  {
    id: 3,
    version: 'v3.1.5',
    date: '2024-01-05',
    title: 'Î≥¥Ïïà ÏóÖÎç∞Ïù¥Ìä∏',
    type: 'security',
    description: 'Î≥¥Ïïà Í¥ÄÎ†® Ï§ëÏöîÌïú ÏóÖÎç∞Ïù¥Ìä∏Í∞Ä Ï†ÅÏö©ÎêòÏóàÏäµÎãàÎã§.',
    details: [
      'Ïù∏Ï¶ù ÏãúÏä§ÌÖú Í∞ïÌôî',
      'Îç∞Ïù¥ÌÑ∞ ÏïîÌò∏Ìôî Í∞úÏÑ†',
      'ÏÑ∏ÏÖò Í¥ÄÎ¶¨ Î≥¥Ïïà Í∞ïÌôî',
      'Ï†ëÍ∑º Ï†úÏñ¥ Ï†ïÏ±Ö ÏóÖÎç∞Ïù¥Ìä∏'
    ],
    changes: [
      { type: 'security', text: 'JWT ÌÜ†ÌÅ∞ Î≥¥Ïïà Í∞ïÌôî' },
      { type: 'security', text: 'SQL Ïù∏Ï†ùÏÖò Î∞©ÏßÄ Í∞úÏÑ†' },
      { type: 'fix', text: 'Î°úÍ∑∏Ïù∏ ÏÑ∏ÏÖò Í¥ÄÎ¶¨ Î≤ÑÍ∑∏ ÏàòÏ†ï' }
    ]
  },
  {
    id: 4,
    version: 'v3.1.0',
    date: '2023-12-20',
    title: 'ÎåÄÍ∑úÎ™® ÏóÖÎç∞Ïù¥Ìä∏',
    type: 'major',
    description: 'ÏãúÏä§ÌÖú Ï†ÑÏ≤¥Ï†ÅÏù∏ Í∞úÌé∏Ïù¥ Ïù¥Î£®Ïñ¥Ï°åÏäµÎãàÎã§.',
    details: [
      'ÏÉàÎ°úÏö¥ Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Íµ¨Ï°∞ ÎèÑÏûÖ',
      'API ÏóîÎìúÌè¨Ïù∏Ìä∏ Ïû¨ÏÑ§Í≥Ñ',
      'ÏÇ¨Ïö©Ïûê Ïù∏ÌÑ∞ÌéòÏù¥Ïä§ ÏôÑÏ†Ñ Í∞úÌé∏',
      'ÏÑ±Îä• Î™®ÎãàÌÑ∞ÎßÅ ÏãúÏä§ÌÖú ÎèÑÏûÖ'
    ],
    changes: [
      { type: 'major', text: 'Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Íµ¨Ï°∞ ÏôÑÏ†Ñ Ïû¨ÏÑ§Í≥Ñ' },
      { type: 'major', text: 'API v2.0ÏúºÎ°ú ÏóÖÍ∑∏Î†àÏù¥Îìú' },
      { type: 'feature', text: 'Ïã§ÏãúÍ∞Ñ Î™®ÎãàÌÑ∞ÎßÅ ÎåÄÏãúÎ≥¥Îìú Ï∂îÍ∞Ä' }
    ]
  },
  {
    id: 5,
