      { type: 'improvement', text: 'Ï†ëÍ∑ºÏÑ± Í∞ÄÏù¥ÎìúÎùºÏù∏ Ï§ÄÏàò' }
    ]
  }
];

function UpdateHistory() {
  const [selectedUpdate, setSelectedUpdate] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedType, setSelectedType] = useState('all');
  const [selectedYear, setSelectedYear] = useState('all');

  const handleUpdateClick = (update) => {
    setSelectedUpdate(update);
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setSelectedUpdate(null);
  };

  const getTypeIcon = (type) => {
    switch (type) {
      case 'feature':
        return 'üÜï';
      case 'improvement':
        return '‚ö°';
      case 'security':
        return 'üîí';
      case 'major':
        return 'üöÄ';
      default:
        return '‚ÑπÔ∏è';
    }
  };

  const getTypeClass = (type) => {
    switch (type) {
      case 'feature':
        return 'type-feature';
      case 'improvement':
        return 'type-improvement';
      case 'security':
        return 'type-security';
      case 'major':
        return 'type-major';
      default:
        return 'type-default';
    }
  };

  const getChangeTypeClass = (type) => {
    switch (type) {
      case 'feature':
        return 'change-feature';
      case 'fix':
        return 'change-fix';
      case 'improvement':
        return 'change-improvement';
      case 'security':
        return 'change-security';
      case 'major':
        return 'change-major';
      default:
        return 'change-default';
    }
  };

  // Í≥†Ïú†Ìïú ÌÉÄÏûÖÍ≥º Ïó∞ÎèÑ Î™©Î°ù ÏÉùÏÑ±
  const uniqueTypes = useMemo(() => {
    const types = [...new Set(updateHistory.map(update => update.type))];
    return types;
  }, []);

  const uniqueYears = useMemo(() => {
    const years = [...new Set(updateHistory.map(update => update.date.split('-')[0]))];
    return years.sort((a, b) => b - a); // ÏµúÏã† Ïó∞ÎèÑÎ∂ÄÌÑ∞ Ï†ïÎ†¨
  }, []);

  // ÌïÑÌÑ∞ÎßÅÎêú ÏóÖÎç∞Ïù¥Ìä∏ Î™©Î°ù
  const filteredUpdates = useMemo(() => {
    return updateHistory.filter(update => {
      const matchesSearch = searchTerm === '' || 
        update.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        update.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        update.details.some(detail => detail.toLowerCase().includes(searchTerm.toLowerCase()));

      const matchesType = selectedType === 'all' || update.type === selectedType;
      const matchesYear = selectedYear === 'all' || update.date.startsWith(selectedYear);

      return matchesSearch && matchesType && matchesYear;
    });
  }, [searchTerm, selectedType, selectedYear]);

  const clearFilters = () => {
    setSearchTerm('');
    setSelectedType('all');
    setSelectedYear('all');
  };

  return (
    <div className="App">
      <header className="app-header">
        <div className="header-content">
          <span className="header-icon">üìã</span>
          <h1>IMT v3.0 ÏóÖÎç∞Ïù¥Ìä∏ ÌûàÏä§ÌÜ†Î¶¨</h1>
        </div>
      </header>

      <main className="main-content">
        <div className="container">
          <h2 className="page-title">ÏóÖÎç∞Ïù¥Ìä∏ ÎÇ¥Ïó≠</h2>

          <p className="page-description">
            IMT v3.0Ïùò Î™®Îì† ÏóÖÎç∞Ïù¥Ìä∏ ÎÇ¥Ïó≠ÏùÑ ÌôïÏù∏Ìï† Ïàò ÏûàÏäµÎãàÎã§. Í∞Å ÏóÖÎç∞Ïù¥Ìä∏Î•º ÌÅ¥Î¶≠ÌïòÏó¨ ÏûêÏÑ∏Ìïú ÎÇ¥Ïö©ÏùÑ ÌôïÏù∏ÌïòÏÑ∏Ïöî.
          </p>

          {/* ÌïÑÌÑ∞ ÏÑπÏÖò */}
          <div className="filters-section">
            <div className="search-box">
              <span className="search-icon">üîç</span>
              <input
                type="text"
                placeholder="Ï†úÎ™©, ÏÑ§Î™Ö, ÎÇ¥Ïö©ÏúºÎ°ú Í≤ÄÏÉâ..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
            </div>

            <div className="filter-controls">
              <div className="filter-group">
                <label className="filter-label">ÌÉÄÏûÖ:</label>
                <select 
                  value={selectedType} 
                  onChange={(e) => setSelectedType(e.target.value)}
                  className="filter-select"
                >
                  <option value="all">Î™®Îì† ÌÉÄÏûÖ</option>
                  {uniqueTypes.map(type => (
                    <option key={type} value={type}>
                      {type === 'feature' ? 'Í∏∞Îä• Ï∂îÍ∞Ä' :
                       type === 'improvement' ? 'Í∞úÏÑ†' :
                       type === 'security' ? 'Î≥¥Ïïà' :
                       type === 'major' ? 'ÎåÄÍ∑úÎ™®' : type}
                    </option>
                  ))}
                </select>
              </div>

              <div className="filter-group">
                <label className="filter-label">Ïó∞ÎèÑ:</label>
                <select 
                  value={selectedYear} 
                  onChange={(e) => setSelectedYear(e.target.value)}
                  className="filter-select"
                >
                  <option value="all">Î™®Îì† Ïó∞ÎèÑ</option>
                  {uniqueYears.map(year => (
                    <option key={year} value={year}>{year}ÎÖÑ</option>
                  ))}
                </select>
              </div>

              <button onClick={clearFilters} className="clear-filters-btn">
                ÌïÑÌÑ∞ Ï¥àÍ∏∞Ìôî
              </button>
            </div>

            {/* Í≤∞Í≥º Ïπ¥Ïö¥Ìä∏ */}
            <div className="results-count">
              Ï¥ù {filteredUpdates.length}Í∞úÏùò ÏóÖÎç∞Ïù¥Ìä∏Î•º Ï∞æÏïòÏäµÎãàÎã§.
            </div>
          </div>

          <div className="updates-grid">
            {filteredUpdates.length > 0 ? (
              filteredUpdates.map((update) => (
                <div 
                  key={update.id} 
                  className="update-card"
                  onClick={() => handleUpdateClick(update)}
                >
                  <div className="card-header">
                    <div className="card-title-section">
                      <span className="type-icon">{getTypeIcon(update.type)}</span>
                      <h3 className="card-title">{update.title}</h3>
                    </div>
                    <span className={`version-badge ${getTypeClass(update.type)}`}>
                      {update.version}
                    </span>
                  </div>

                  <p className="card-description">{update.description}</p>

                  <div className="card-footer">
                    <span className="date-icon">üìÖ</span>
                    <span className="date-text">{update.date}</span>
                  </div>
                </div>
              ))
            ) : (
              <div className="no-results">
                <span className="no-results-icon">üîç</span>
                <h3>Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§</h3>
                <p>Îã§Î•∏ Í≤ÄÏÉâÏñ¥ÎÇò ÌïÑÌÑ∞Î•º ÏãúÎèÑÌï¥Î≥¥ÏÑ∏Ïöî.</p>
              </div>
            )}
          </div>
        </div>
      </main>

      {openDialog && selectedUpdate && (
        <div className="modal-overlay" onClick={handleCloseDialog}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <div className="modal-title-section">
                <span className="modal-type-icon">{getTypeIcon(selectedUpdate.type)}</span>
                <h2 className="modal-title">{selectedUpdate.title}</h2>
              </div>
              <button className="close-button" onClick={handleCloseDialog}>
                ‚úï
              </button>
            </div>

            <div className="modal-badge-section">
              <span className={`modal-version-badge ${getTypeClass(selectedUpdate.type)}`}>
                {selectedUpdate.version}
              </span>
              <span className="modal-date">{selectedUpdate.date}</span>
            </div>

            <div className="modal-body">
              <p className="modal-description">{selectedUpdate.description}</p>

              <h3 className="section-title">Ï£ºÏöî Î≥ÄÍ≤ΩÏÇ¨Ìï≠</h3>

              <ul className="details-list">
                {selectedUpdate.details.map((detail, index) => (
                  <li key={index} className="detail-item">
                    <span className="detail-icon">‚ÑπÔ∏è</span>
                    {detail}
                  </li>
                ))}
              </ul>

              <hr className="divider" />

              <h3 className="section-title">ÏÉÅÏÑ∏ Î≥ÄÍ≤ΩÎÇ¥Ïó≠</h3>

              <div className="changes-container">
                {selectedUpdate.changes.map((change, index) => (
                  <span 
                    key={index}
                    className={`change-chip ${getChangeTypeClass(change.type)}`}
                  >
                    {change.text}
                  </span>
                ))}
              </div>
            </div>

            <div className="modal-footer">
              <button className="close-modal-button" onClick={handleCloseDialog}>
                Îã´Í∏∞
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

function App() {
  return (
    <Router>
      <div className="App">
        <nav className="navigation">
          <div className="nav-container">
