import React, { useState } from 'react';
import './DataExport.css';

function DataExport() {
  const [selectedFormat, setSelectedFormat] = useState('excel');
  const [selectedData, setSelectedData] = useState('all');
  const [dateRange, setDateRange] = useState({ start: '', end: '' });
  const [isExporting, setIsExporting] = useState(false);
  const [exportHistory, setExportHistory] = useState([
    {
      id: 1,
      fileName: 'IMT_Update_History_2024_01_15.xlsx',
      format: 'Excel',
      dataType: 'Ï†ÑÏ≤¥ Îç∞Ïù¥ÌÑ∞',
      dateRange: '2024-01-01 ~ 2024-01-15',
      exportDate: '2024-01-15 14:30',
      status: 'ÏôÑÎ£å',
      fileSize: '2.3 MB'
    },
    {
      id: 2,
      fileName: 'IMT_Security_Updates_2024_01_10.csv',
      format: 'CSV',
      dataType: 'Î≥¥Ïïà ÏóÖÎç∞Ïù¥Ìä∏',
      dateRange: '2024-01-01 ~ 2024-01-10',
      exportDate: '2024-01-10 11:45',
      status: 'ÏôÑÎ£å',
      fileSize: '1.1 MB'
    },
    {
      id: 3,
      fileName: 'IMT_Feature_Updates_2024_01_05.json',
      format: 'JSON',
      dataType: 'Í∏∞Îä• ÏóÖÎç∞Ïù¥Ìä∏',
      dateRange: '2024-01-01 ~ 2024-01-05',
      exportDate: '2024-01-05 09:20',
      status: 'ÏôÑÎ£å',
      fileSize: '856 KB'
    }
  ]);

  const handleExport = () => {
    setIsExporting(true);
    
    // ÏãúÎÆ¨Î†àÏù¥ÏÖò: 3Ï¥à ÌõÑ ÏôÑÎ£å
    setTimeout(() => {
      const newExport = {
        id: exportHistory.length + 1,
        fileName: `IMT_Export_${new Date().toISOString().split('T')[0]}.${selectedFormat === 'excel' ? 'xlsx' : selectedFormat === 'csv' ? 'csv' : 'json'}`,
        format: selectedFormat === 'excel' ? 'Excel' : selectedFormat === 'csv' ? 'CSV' : 'JSON',
        dataType: selectedData === 'all' ? 'Ï†ÑÏ≤¥ Îç∞Ïù¥ÌÑ∞' : 
                  selectedData === 'security' ? 'Î≥¥Ïïà ÏóÖÎç∞Ïù¥Ìä∏' :
                  selectedData === 'feature' ? 'Í∏∞Îä• ÏóÖÎç∞Ïù¥Ìä∏' :
                  selectedData === 'improvement' ? 'Í∞úÏÑ† ÏÇ¨Ìï≠' : 'ÎåÄÍ∑úÎ™® ÏóÖÎç∞Ïù¥Ìä∏',
        dateRange: dateRange.start && dateRange.end ? `${dateRange.start} ~ ${dateRange.end}` : 'Ï†ÑÏ≤¥ Í∏∞Í∞Ñ',
        exportDate: new Date().toLocaleString('ko-KR'),
        status: 'ÏôÑÎ£å',
        fileSize: `${Math.floor(Math.random() * 3 + 1)}.${Math.floor(Math.random() * 9)} MB`
      };
      
      setExportHistory([newExport, ...exportHistory]);
      setIsExporting(false);
      alert('Îç∞Ïù¥ÌÑ∞ Ï∂îÏ∂úÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!');
    }, 3000);
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'ÏôÑÎ£å':
        return '‚úÖ';
      case 'ÏßÑÌñâÏ§ë':
        return '‚è≥';
      case 'Ïã§Ìå®':
        return '‚ùå';
      default:
        return '‚ÑπÔ∏è';
    }
  };

  const getFormatIcon = (format) => {
    switch (format) {
      case 'Excel':
        return 'üìä';
      case 'CSV':
        return 'üìã';
      case 'JSON':
        return 'üîß';
      default:
        return 'üìÑ';
    }
  };

  return (
    <div className="data-export-page">
      <header className="export-header">
        <h1>üìä Îç∞Ïù¥ÌÑ∞ Ï∂îÏ∂ú</h1>
        <p>IMT v3.0 ÏãúÏä§ÌÖúÏùò ÏóÖÎç∞Ïù¥Ìä∏ Îç∞Ïù¥ÌÑ∞Î•º Îã§ÏñëÌïú ÌòïÏãùÏúºÎ°ú Ï∂îÏ∂úÌï† Ïàò ÏûàÏäµÎãàÎã§.</p>
      </header>

      <main className="export-content">
        <div className="export-container">
          <section className="export-settings">
            <h2>‚öôÔ∏è Ï∂îÏ∂ú ÏÑ§Ï†ï</h2>
            
            <div className="setting-group">
              <label className="setting-label">üìÑ Ï∂úÎ†• ÌòïÏãù:</label>
              <select 
                value={selectedFormat} 
                onChange={(e) => setSelectedFormat(e.target.value)}
                className="setting-select"
              >
                <option value="excel">Excel (.xlsx)</option>
                <option value="csv">CSV (.csv)</option>
                <option value="json">JSON (.json)</option>
              </select>
            </div>

            <div className="setting-group">
